<?php	session_start();		?><!DOCTYPE hmtl><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1/EN""http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd"><html><head><!--	file name: product.php -->	<title>Tuna Cafe</title>	<link href="php.css" rel="stylesheet" type="text/css" /></head><body><h2>Welcome to Tuna Cafe!</h2><form action="confirmed.php" method="POST"><table border="0" style="width:300px"><?php	//$date=date("F j, Y");	date_default_timezone_set('America/Los_Angeles');		$date = date("Y-m-d");		$customerid = $_POST['customerid'];	$_SESSION['customerid'] = $customerid;	$_SESSION['orderdate'] = $date;	$invCustomer = $_SESSION['invCustomer'];	$invTuna = $_SESSION['invTuna'];		$name = $invCustomer[$customerid]['name'];	$street = $invCustomer[$customerid]['street'];	$city = $invCustomer[$customerid]['city'];	$state = $invCustomer[$customerid]['state'];	$zip = $invCustomer[$customerid]['zip'];		echo "<tr><td class='left'><span class='fmlabel'>Order Date:</span>$date<br />";	echo "<span class='fmlabel'>Customer Name:</span><br />";	echo "$name<br/>";	echo "$street<br />";	echo "$city, $state $zip</td></tr>";?>					<tr><td colspan='2' class='left'>	<fieldset><legend><span class="fmlabel">Tuna! Tuna! Tuna!</span></legend>	<?php		foreach($invTuna as $tunaID => $item)		{			$dish = $item['dish'];			$price = $item['price'];			echo "<input type='checkbox' name='ordered[]' value='$tunaID'>$dish ($$price)<br />";		}	?>			</fieldset></td></tr>	<tr><td colspan='2' class='center'>			<input type="submit" value="Submit"><input type="reset" value="Cancel">	</td></tr></table></form></body></html>